<?phpinclude_once('m/model.php');include_once('c/C_Base.php');class C_Article extends C_Base{	private $article;	function __construct()	{			}	protected function OnInput()	{		parent::OnInput();        $this->article = get_article($_GET['id']);		$this->title = $this->title . ": #" . $_GET['id'];	}			protected function OnOutput()	{		$vars = array('article' => $this->article);		$this->content = $this->Template('v/v_article.php', $vars);		parent::OnOutput();	}	}