<?phpinclude_once('c/Controller.php');include('m/db_connect.php');abstract class C_Base extends Controller{	protected $title;			protected $content;			function __construct()	{			}		protected function OnInput()	{		$this->title = 'Blog';		$this->content = '';	}		protected function OnOutput()	{		$vars = array('title' => $this->title, 'content' => $this->content);			$page = $this->Template('v/v_base.php', $vars);		echo $page;	}	}