<?phpinclude_once('m/article_model.php');include_once('c/C_Base.php');class C_Editing extends C_Base{	private $article;		function __construct()	{			}		protected function OnInput()	{		parent::OnInput();		$this->title = $this->title . ': Editing';		$this->article = ArticleModel::get_article($_GET['id']);        if (isset($_POST['delete']))        {            ArticleModel::delete_article($this->article['id_article']);            header('Location: index.php?c=edit');            die();        }	if (isset($_POST['title']) && isset($_POST['title']))	{		if (ArticleModel::article_update($_POST['title'], $_POST['content'], $_GET['id']))		{			header('Location: index.php?c=editor');			die();		}		$title = $_POST['title'];	$content = $_POST['content'];	}	}		protected function OnOutput()	{		$vars = array('article' => $this->article);			$this->content = $this->Template('v/v_edit.php', $vars);		parent::OnOutput();	}	}