<?phpinclude_once('m/article_model.php');include_once('c/C_Base.php');class C_Editor extends C_Base{	private $articles;		function __construct()	{			}		protected function OnInput()	{		parent::OnInput();		$this->title = $this->title . ': Editor';		$this->articles = ArticleModel::get_articles();	}		protected function OnOutput()	{		$vars = array('articles' => $this->articles);			$this->content = $this->Template('v/v_editor.php', $vars);		parent::OnOutput();	}	}