<?phpinclude_once('m/model.php');include_once('c/C_Base.php');class C_New extends C_Base{    private  $article;	function __construct()	{			}		protected function OnInput()	{		parent::OnInput();		$this->title = $this->title . ': New article';	if (isset($_POST['title']) && isset($_POST['title']))	{		if (articles_new($_POST['title'], $_POST['content']))		{			header('Location: index.php?c=edit');			die();		}		$title = $_POST['title'];	$content = $_POST['content'];	}	}		protected function OnOutput()	{		$vars = array();		$this->content = $this->Template('v/v_new.php', $vars);		parent::OnOutput();	}	}