<?phpinclude_once('m/article_model.php');include_once('c/C_Base.php');class C_View extends C_Base{	private $articles;	function __construct()	{			}	protected function OnInput()	{		parent::OnInput();		$this->title = $this->title . ': latest articles';		$this->articles = ArticleModel::get_articles();	}			protected function OnOutput()	{		$vars = array('articles' => $this->articles);			$this->content = $this->Template('v/v_view.php', $vars);		parent::OnOutput();	}	}